function responsiveNavOpen(){$(".btn-responsive-nav").on("click",function(n){$(".navbar ul").toggleClass("nav-close nav-open"),n.preventDefault()})}function responsiveNavClose(){$(".btn-responsive-nav-close").on("click",function(n){$(".navbar ul").toggleClass("nav-close nav-open"),n.preventDefault()})}function dropdownMenu(){$(".dropdown-menu").addClass("dropdown-menu-inactive"),$("body").on("click",".btn-dropdown",function(n){var e=$(this),o=$("#"+e.data("for"));o.toggleClass("dropdown-menu-inactive dropdown-menu-active"),n.preventDefault()})}$(document).ready(function(){"use strict";dropdownMenu(),responsiveNavOpen(),responsiveNavClose()});